<?PHP/*Plugin Name: HTML5 Details PolyfillPlugin URI: http://JoeAnzalone.com/plugins/html5-details-polyfillDescription: Adds fallback support for the HTML5 <a rel="external" href="http://html5doctor.com/the-details-and-summary-elements/">&lt;details&gt; and &lt;summary&gt; elements</a> in browsers that don't support them using <a rel="external" href="http://mathiasbynens.be/notes/html5-details-jquery">Mathias Bynens' &lt;details&gt;/&lt;summary&gt; jQuery plugin.</a>Version: 1.0Author: Joe AnzaloneAuthor URI: http://JoeAnzalone.comLicense: GPL2*//*This WordPress plugin uses Mathias Bynens' jQuery plugin available at:http://mathiasbynens.be/notes/html5-details-jquery"The plugin will automatically detect browser support and act accordingly. If other parts of your code need to know whether <details>/<summary> are supported or not, use $.fn.details.support"*/?><?PHPclass shmit_html5_details_polyfill {		function head_scripts(){		wp_enqueue_script('jquery-details', $this->plugin_url . '/js/jquery.details.min.js');				wp_enqueue_script( 'html5-details-polyfill-main', $this->plugin_url . '/js/main.js', 'jquery-details' );				/* This stylesheet is a simplified version of the stylesheet included in Mathias Bynens' demo.		It's been designed to be as bare-bones as possible so as to not get in the way of the theme design		See the demo here: http://mathiasbynens.be/demo/html5-details-jquery */		wp_enqueue_style( 'html5-details-polyfill-main', $this->plugin_url . '/css/main.css' );			}		function __construct(){		$this->plugin_url = plugins_url(NULL, __FILE__);		add_action('wp_head', array($this,'head_scripts'));	}}new shmit_html5_details_polyfill;?>